import React, { useRef, useState, useEffect } from 'react';
import { processSVGData, generateSVGContent } from '../utils/svgProcessor';
import './WorldMap.css';

function WorldMap({ selectedLocations = [], setSelectedLocations = () => {}, onLocationClick = () => {} }) {
  const [hoveredName, setHoveredName] = useState('');
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const [svgContent, setSvgContent] = useState('');
  const [transform, setTransform] = useState({ x: 0, y: 0, scale: 1 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const svgRef = useRef();

  useEffect(() => {
    // Process the SVG data and generate the content
    const rawSVGData = document.querySelector('svg').outerHTML;
    const { countries } = processSVGData(rawSVGData);
    const content = generateSVGContent(countries);
    setSvgContent(content);
  }, []);

  function handleLocationClick(event) {
    const id = event.target.id;
    const name = event.target.getAttribute('title');
    if (!id || !name) return;

    setSelectedLocations(prev => {
      const exists = prev.find(s => s.id === id);
      if (exists) {
        return prev.filter(s => s.id !== id);
      } else {
        return [...prev, { id, name }];
      }
    });
    onLocationClick({ id, name });
  }

  function onLocationMouseOver(event) {
    const name = event.target.getAttribute('title');
    if (name) {
      setHoveredName(name);
    }
  }

  function onLocationMouseOut() {
    setHoveredName('');
  }

  function onMouseMove(event) {
    setMousePos({ x: event.clientX, y: event.clientY });

    if (isDragging) {
      const dx = event.clientX - dragStart.x;
      const dy = event.clientY - dragStart.y;
      setTransform(prev => ({
        ...prev,
        x: prev.x + dx,
        y: prev.y + dy
      }));
      setDragStart({ x: event.clientX, y: event.clientY });
    }
  }

  function onMouseDown(event) {
    if (event.button === 0) { // Left mouse button
      setIsDragging(true);
      setDragStart({ x: event.clientX, y: event.clientY });
    }
  }

  function onMouseUp() {
    setIsDragging(false);
  }

  function onWheel(event) {
    event.preventDefault();
    const delta = event.deltaY;
    const scale = delta > 0 ? 0.9 : 1.1;

    // Calculate the mouse position relative to the SVG
    const svg = svgRef.current.querySelector('svg');
    const rect = svg.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // Calculate the new transform
    setTransform(prev => ({
      x: prev.x - (x * (scale - 1)),
      y: prev.y - (y * (scale - 1)),
      scale: prev.scale * scale
    }));
  }

  function handleZoomIn() {
    setTransform(prev => ({
      ...prev,
      scale: Math.min(prev.scale * 1.2, 5)
    }));
  }

  function handleZoomOut() {
    setTransform(prev => ({
      ...prev,
      scale: Math.max(prev.scale * 0.8, 0.5)
    }));
  }

  function handleReset() {
    setTransform({ x: 0, y: 0, scale: 1 });
  }

  return (
    <div 
      className="world-map-container"
      onMouseMove={onMouseMove}
      onMouseDown={onMouseDown}
      onMouseUp={onMouseUp}
      onWheel={onWheel}
      ref={svgRef}
    >
      <div style={{ position: 'relative', width: '100%', height: '100%' }}>
        <svg
          viewBox="0 0 3000 1500"
          className="world-map-svg"
          onClick={handleLocationClick}
          onMouseOver={onLocationMouseOver}
          onMouseOut={onLocationMouseOut}
          style={{
            transform: `translate(${transform.x}px, ${transform.y}px) scale(${transform.scale})`,
            transformOrigin: '0 0'
          }}
          dangerouslySetInnerHTML={{ __html: svgContent }}
        />
      </div>
      <div className="zoom-controls">
        <button className="zoom-button" onClick={handleZoomIn} title="Zoom In">+</button>
        <button className="zoom-button" onClick={handleZoomOut} title="Zoom Out">−</button>
        <button className="zoom-button" onClick={handleReset} title="Reset View">↺</button>
      </div>
      {hoveredName && (
        <div
          className="world-map-tooltip"
          style={{
            position: 'fixed',
            left: mousePos.x + 12,
            top: mousePos.y + 12,
            background: '#222',
            color: '#fff',
            padding: '4px 10px',
            borderRadius: 4,
            pointerEvents: 'none',
            fontSize: 14,
            zIndex: 1000,
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
          }}
        >
          {hoveredName}
        </div>
      )}
    </div>
  );
}

export default WorldMap;
