import React, { useEffect, useRef, useState } from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

export default function WorldMapView() {
  const [time, setTime] = useState(new Date());
  const [selectedCountry, setSelectedCountry] = useState(null);
  const svgRef = useRef();

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  // Attach click handlers to all country paths after SVG loads
  useEffect(() => {
    const svg = svgRef.current;
    if (!svg) return;
    // Select all paths with class 'landxx' (countries)
    const countryPaths = svg.querySelectorAll('path.landxx');
    countryPaths.forEach(path => {
      path.style.cursor = 'pointer';
      path.onclick = (e) => {
        // Remove highlight from all
        countryPaths.forEach(p => p.style.stroke = '#ffffff');
        // Highlight selected
        path.style.stroke = '#1976d2';
        path.style.strokeWidth = 2;
        // Get country name from <title> or id
        const title = path.querySelector('title');
        const countryName = title ? title.textContent : path.id;
        setSelectedCountry(countryName);
      };
    });
  }, [svgRef]);

  return (
    <div className="container" style={{ maxWidth: 900, margin: '0 auto', padding: 24 }}>
      <h1 style={{ textAlign: 'center', marginBottom: 16 }}>World Map Time</h1>
      <div id="clock" style={{ textAlign: 'center', fontSize: 24, marginBottom: 16 }}>
        {time.toLocaleTimeString()} (Local Time)
      </div>
      <div style={{ textAlign: 'center', margin: '24px 0' }}>
        <object
          ref={svgRef}
          type="image/svg+xml"
          data={require('../assets/BlankMap-World.svg').default}
          style={{ width: '100%', maxWidth: 900, height: 'auto', border: '1px solid #eee', borderRadius: 8 }}
          aria-label="World SVG Map"
        />
      </div>
      {selectedCountry && (
        <div style={{ textAlign: 'center', marginTop: 16 }}>
          <strong>Selected Country:</strong> {selectedCountry}
        </div>
      )}
    </div>
  );
}
