import React, { useState, useEffect } from 'react';
import API from '../api/api';
import Layout from './Layout';
import './TravelJournal.css';

function getAuthHeader() {
  const token = localStorage.getItem('token');
  return token ? { Authorization: token } : {};
}

const AddEditModal = ({ open, onClose, onSave, initialValue, title }) => {
  const [value, setValue] = useState(initialValue || '');
  useEffect(() => { setValue(initialValue || ''); }, [initialValue]);
  if (!open) return null;
  return (
    <div className="tj-modal-backdrop" onClick={onClose}>
      <div className="tj-modal" onClick={e => e.stopPropagation()}>
        <h3>{title}</h3>
        <input
          className="tj-modal-input"
          value={value}
          onChange={e => setValue(e.target.value)}
          autoFocus
        />
        <div className="tj-modal-actions">
          <button onClick={onClose} className="tj-btn tj-btn-cancel">Cancel</button>
          <button onClick={() => { if (value.trim()) onSave(value.trim()); }} className="tj-btn tj-btn-save">Save</button>
        </div>
      </div>
    </div>
  );
};

const TravelJournal = () => {
  const [destinations, setDestinations] = useState([]);
  const [visited, setVisited] = useState([]); // [{state, destination}]
  const [expanded, setExpanded] = useState({}); // { [stateId]: true/false }
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('all');
  const [modal, setModal] = useState({ open: false });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchAll() {
      setLoading(true);
      try {
        const [dRes, vRes] = await Promise.all([
          API.get('/api/destinations', { headers: getAuthHeader() }),
          API.get('/api/user/visited-destinations', { headers: getAuthHeader() })
        ]);
        setDestinations(dRes.data);
        setVisited(vRes.data.visitedDestinations || []);
      } catch (err) {
        setDestinations([]);
        setVisited([]);
      } finally {
        setLoading(false);
      }
    }
    fetchAll();
  }, []);

  const isVisited = (state, dest) => visited.some(v => v.state === state && v.destination === dest);

  const handleToggleVisited = async (state, dest) => {
    try {
      await API.post('/api/user/visited-destinations', { state, destination: dest }, { headers: getAuthHeader() });
      setVisited(prev =>
        isVisited(state, dest)
          ? prev.filter(v => !(v.state === state && v.destination === dest))
          : [...prev, { state, destination: dest }]
      );
    } catch (err) {
      alert('Failed to update visited status');
    }
  };

  // Add/Edit/Delete destination
  const handleAddDestination = async (stateId, newDest) => {
    try {
      await API.post(`/api/destinations/${stateId}/destination`, { destination: newDest }, { headers: getAuthHeader() });
      setDestinations(ds => ds.map(d => d._id === stateId ? { ...d, destinations: [...d.destinations, newDest] } : d));
      setModal({ open: false });
    } catch {
      alert('Failed to add destination');
    }
  };
  const handleEditDestination = async (stateId, oldName, newName) => {
    try {
      await API.put(`/api/destinations/${stateId}/destination`, { oldName, newName }, { headers: getAuthHeader() });
      setDestinations(ds => ds.map(d =>
        d._id === stateId
          ? {
              ...d,
              destinations: d.destinations.map(n => n === oldName ? newName : n),
              topDestinations: d.topDestinations.map(n => n === oldName ? newName : n)
            }
          : d
      ));
      setModal({ open: false });
    } catch {
      alert('Failed to edit destination');
    }
  };
  const handleDeleteDestination = async (stateId, name) => {
    if (!window.confirm('Delete this destination?')) return;
    try {
      await API.delete(`/api/destinations/${stateId}/destination`, { data: { name }, headers: getAuthHeader() });
      setDestinations(ds => ds.map(d =>
        d._id === stateId
          ? {
              ...d,
              destinations: d.destinations.filter(n => n !== name),
              topDestinations: d.topDestinations.filter(n => n !== name)
            }
          : d
      ));
    } catch {
      alert('Failed to delete destination');
    }
  };

  // Filtering
  const filteredDestinations = destinations.filter(dest => {
    const matchesSearch = dest.state.toLowerCase().includes(searchTerm.toLowerCase()) ||
      dest.destinations.some(d => d.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesFilter = filter === 'all' ||
      (filter === 'visited' && dest.destinations.some(d => isVisited(dest.state, d))) ||
      (filter === 'unvisited' && !dest.destinations.some(d => isVisited(dest.state, d)));
    return matchesSearch && matchesFilter;
  });

  const getProgress = () => {
    const total = destinations.reduce((acc, d) => acc + d.destinations.length, 0);
    const visitedCount = visited.length;
    return total ? Math.round((visitedCount / total) * 100) : 0;
  };

  // Expand/collapse logic
  const toggleExpand = (id) => setExpanded(prev => ({ ...prev, [id]: !prev[id] }));

  return (
    <Layout>
      <div className="travel-journal">
        <div className="journal-header">
          <h1>Travel Journal</h1>
          <div className="progress-container">
            <div className="progress-bar">
              <div className="progress-fill" style={{ width: `${getProgress()}%` }} />
            </div>
            <span className="progress-text">
              {visited.length} destinations visited
            </span>
          </div>
        </div>
        <div className="journal-controls">
          <input
            type="text"
            placeholder="Search destinations..."
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <select
            value={filter}
            onChange={e => setFilter(e.target.value)}
            className="filter-select"
          >
            <option value="all">All Destinations</option>
            <option value="visited">Visited</option>
            <option value="unvisited">Unvisited</option>
          </select>
        </div>
        {loading ? (
          <div style={{ textAlign: 'center', margin: '2rem', color: '#888' }}>Loading...</div>
        ) : (
          <div className="destinations-grid">
            {filteredDestinations.map((dest) => {
              const visitedTop = dest.topDestinations.filter(place => isVisited(dest.state, place)).length;
              const visitedAll = dest.destinations.filter(place => isVisited(dest.state, place)).length;
              const isOpen = expanded[dest._id];
              return (
                <div
                  key={dest._id}
                  className={`destination-card${isOpen ? ' expanded' : ''}`}
                  onClick={() => toggleExpand(dest._id)}
                  style={{ cursor: 'pointer' }}
                >
                  <div className="card-header">
                    <h2>{dest.state}</h2>
                    <span className="type-badge">{dest.type}</span>
                    <span style={{ marginLeft: 'auto', fontWeight: 600, color: '#4f8cff', fontSize: '0.95em' }}>
                      <span title="Top 3 visited">{visitedTop} / {dest.topDestinations.length} ⭐</span>
                      <span style={{ marginLeft: 10 }} title="All visited">{visitedAll} / {dest.destinations.length} ✓</span>
                    </span>
                    <button
                      className="tj-btn tj-btn-add"
                      title="Add destination"
                      onClick={e => { e.stopPropagation(); setModal({ open: true, stateId: dest._id, type: 'add' }); }}
                    >+
                    </button>
                    <button
                      className="tj-btn"
                      style={{ marginLeft: 6, background: isOpen ? '#e3eafc' : '#fff', color: '#4f8cff', border: '1px solid #e3eafc' }}
                      title={isOpen ? 'Collapse' : 'Expand'}
                      onClick={e => { e.stopPropagation(); toggleExpand(dest._id); }}
                    >{isOpen ? '−' : '+'}</button>
                  </div>
                  {isOpen && (
                    <div className="destinations-list">
                      {dest.destinations.map((place) => (
                        <div
                          key={place}
                          className={`destination-item ${
                            dest.topDestinations.includes(place) ? 'top-destination' : ''
                          } ${isVisited(dest.state, place) ? 'visited' : ''}`}
                          style={{ background: isVisited(dest.state, place) ? '#e6ffe6' : '#fffbe6' }}
                          onClick={e => { e.stopPropagation(); handleToggleVisited(dest.state, place); }}
                        >
                          <span className="destination-name">{place}</span>
                          {dest.topDestinations.includes(place) && (
                            <span className="top-badge">⭐</span>
                          )}
                          <span className="visit-status">
                            {isVisited(dest.state, place) ? '✓' : '○'}
                          </span>
                          <span className="tj-dest-actions">
                            <span
                              className="tj-dest-edit"
                              title="Edit"
                              onClick={e => { e.stopPropagation(); setModal({ open: true, stateId: dest._id, type: 'edit', oldName: place }); }}
                            >✏️</span>
                            <span
                              className="tj-dest-delete"
                              title="Delete"
                              onClick={e => { e.stopPropagation(); handleDeleteDestination(dest._id, place); }}
                            >🗑️</span>
                          </span>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}
        {/* Add/Edit Modal */}
        <AddEditModal
          open={modal.open}
          title={modal.type === 'add' ? 'Add Destination' : 'Edit Destination'}
          initialValue={modal.type === 'edit' ? modal.oldName : ''}
          onClose={() => setModal({ open: false })}
          onSave={val => {
            if (modal.type === 'add') handleAddDestination(modal.stateId, val);
            else if (modal.type === 'edit') handleEditDestination(modal.stateId, modal.oldName, val);
          }}
        />
      </div>
    </Layout>
  );
};

export default TravelJournal;
