import React, { useState, useMemo } from 'react';
import { worldMapSVG } from '../assets/worldMapData';
import './WorldMap.css';

// Robustly add data-name attributes using DOMParser
function addDataNameToSVG(svgString) {
  if (typeof window === 'undefined' || !window.DOMParser) return svgString;
  const parser = new window.DOMParser();
  const doc = parser.parseFromString(svgString, 'image/svg+xml');
  const groups = doc.querySelectorAll('g');
  groups.forEach(g => {
    const title = g.querySelector('title');
    if (title) {
      const name = title.textContent;
      g.querySelectorAll('path, circle').forEach(el => {
        el.setAttribute('data-name', name);
      });
    }
  });
  return doc.documentElement.outerHTML;
}

function WorldMap({ selectedLocations = [], setSelectedLocations = () => {}, onLocationClick = () => {} }) {
  const [hoveredName, setHoveredName] = useState('');
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const [transform, setTransform] = useState({ x: 0, y: 0, scale: 1 });
  const processedSVG = useMemo(() => addDataNameToSVG(worldMapSVG), []);

  function handleLocationClick(event) {
    const id = event.target.id;
    const name = event.target.dataset.name;
    if (!id || !name) return;

    setSelectedLocations(prev => {
      const exists = prev.find(s => s.id === id);
      if (exists) {
        return prev.filter(s => s.id !== id);
      } else {
        return [...prev, { id, name }];
      }
    });
    onLocationClick({ id, name });
  }

  function onLocationMouseOver(event) {
    const name = event.target.dataset.name;
    if (name) {
      setHoveredName(name);
    } else {
      setHoveredName('');
    }
  }

  function onLocationMouseOut() {
    setHoveredName('');
  }

  function handleZoomIn() {
    setTransform(prev => ({
      ...prev,
      scale: Math.min(prev.scale * 1.2, 5)
    }));
  }

  function handleZoomOut() {
    setTransform(prev => ({
      ...prev,
      scale: Math.max(prev.scale * 0.8, 0.5)
    }));
  }

  function handleReset() {
    setTransform({ x: 0, y: 0, scale: 1 });
  }

  return (
    <div 
      className="world-map-container"
    >
      <div 
        className="world-map-svg"
        dangerouslySetInnerHTML={{ __html: processedSVG }}
        onClick={handleLocationClick}
        onMouseOver={onLocationMouseOver}
        onMouseOut={onLocationMouseOut}
        style={{
          transform: `scale(${transform.scale})`,
          transformOrigin: 'center center'
        }}
      />
      <div className="zoom-controls">
        <button className="zoom-button" onClick={handleZoomIn} title="Zoom In">+</button>
        <button className="zoom-button" onClick={handleZoomOut} title="Zoom Out">−</button>
        <button className="zoom-button" onClick={handleReset} title="Reset View">↺</button>
      </div>
      {hoveredName && (
        <div
          className="world-map-tooltip"
          style={{
            left: mousePos.x + 12,
            top: mousePos.y + 12
          }}
        >
          {hoveredName}
        </div>
      )}
    </div>
  );
}

export default WorldMap;
