import React, { useState, useEffect } from 'react';
import API from '../api/api';
import Layout from './Layout';
import './TravelJournal.css';

const TravelJournal = () => {
  const [destinations, setDestinations] = useState([]);
  const [selectedState, setSelectedState] = useState(null);
  const [visitedDestinations, setVisitedDestinations] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('all'); // all, visited, unvisited

  useEffect(() => {
    const fetchDestinations = async () => {
      try {
        const response = await API.get('/api/destinations');
        setDestinations(response.data);
        // Load visited destinations from localStorage
        const savedVisited = JSON.parse(localStorage.getItem('visitedDestinations') || '[]');
        setVisitedDestinations(savedVisited);
      } catch (error) {
        console.error('Error fetching destinations:', error);
      }
    };
    fetchDestinations();
  }, []);

  const toggleDestinationVisit = (destination) => {
    const newVisited = visitedDestinations.includes(destination)
      ? visitedDestinations.filter(d => d !== destination)
      : [...visitedDestinations, destination];
    setVisitedDestinations(newVisited);
    localStorage.setItem('visitedDestinations', JSON.stringify(newVisited));
  };

  const filteredDestinations = destinations
    .filter(dest => {
      const matchesSearch = dest.state.toLowerCase().includes(searchTerm.toLowerCase()) ||
        dest.destinations.some(d => d.toLowerCase().includes(searchTerm.toLowerCase()));
      const matchesFilter = filter === 'all' ||
        (filter === 'visited' && dest.destinations.some(d => visitedDestinations.includes(d))) ||
        (filter === 'unvisited' && !dest.destinations.some(d => visitedDestinations.includes(d)));
      return matchesSearch && matchesFilter;
    });

  const getProgress = (destinations) => {
    const total = destinations.reduce((acc, dest) => acc + dest.destinations.length, 0);
    const visited = visitedDestinations.length;
    return Math.round((visited / total) * 100);
  };

  return (
    <Layout>
      <div className="travel-journal">
        <div className="journal-header">
          <h1>Travel Journal</h1>
          <div className="progress-container">
            <div className="progress-bar">
              <div 
                className="progress-fill"
                style={{ width: `${getProgress(destinations)}%` }}
              />
            </div>
            <span className="progress-text">
              {visitedDestinations.length} destinations visited
            </span>
          </div>
        </div>

        <div className="journal-controls">
          <input
            type="text"
            placeholder="Search destinations..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <select 
            value={filter} 
            onChange={(e) => setFilter(e.target.value)}
            className="filter-select"
          >
            <option value="all">All Destinations</option>
            <option value="visited">Visited</option>
            <option value="unvisited">Unvisited</option>
          </select>
        </div>

        <div className="destinations-grid">
          {filteredDestinations.map((dest) => (
            <div 
              key={dest.state}
              className={`destination-card ${selectedState === dest.state ? 'expanded' : ''}`}
              onClick={() => setSelectedState(selectedState === dest.state ? null : dest.state)}
            >
              <div className="card-header">
                <h2>{dest.state}</h2>
                <span className="type-badge">{dest.type}</span>
              </div>

              <div className="destinations-list">
                {dest.destinations.map((place, index) => (
                  <div 
                    key={place}
                    className={`destination-item ${
                      dest.topDestinations.includes(place) ? 'top-destination' : ''
                    } ${visitedDestinations.includes(place) ? 'visited' : ''}`}
                    onClick={(e) => {
                      e.stopPropagation();
                      toggleDestinationVisit(place);
                    }}
                  >
                    <span className="destination-name">{place}</span>
                    {dest.topDestinations.includes(place) && (
                      <span className="top-badge">⭐</span>
                    )}
                    <span className="visit-status">
                      {visitedDestinations.includes(place) ? '✓' : '○'}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </Layout>
  );
};

export default TravelJournal;
