import React, { useEffect, useState } from 'react';
import API from '../api/api';
import './TravelJournal.css';

export default function TravelJournal() {
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchDestinations() {
      try {
        const res = await API.get('/destinations');
        // Flatten the data
        const flatRows = [];
        (res.data.destinations || []).forEach(doc => {
          (doc.destinations || []).forEach(dest => {
            flatRows.push({
              state: doc.state,
              type: doc.type,
              name: dest.name,
              visited: dest.visited,
              isTop: (doc.topDestinations || []).includes(dest.name)
            });
          });
        });
        setRows(flatRows);
      } catch (err) {
        setError('Failed to load destinations');
      } finally {
        setLoading(false);
      }
    }
    fetchDestinations();
  }, []);

  if (loading) return <div className="tj-loading">Loading...</div>;
  if (error) return <div className="tj-error">{error}</div>;

  return (
    <div className="travel-journal">
      <h2>Travel Journal</h2>
      <table className="tj-table">
        <thead>
          <tr>
            <th>State/UT</th>
            <th>Type</th>
            <th>Destination Name</th>
            <th>Visited</th>
            <th>Top Destination?</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row, idx) => (
            <tr key={row.state + row.name + idx}>
              <td>{row.state}</td>
              <td>{row.type}</td>
              <td>{row.name}</td>
              <td>{row.visited ? 'Yes' : 'No'}</td>
              <td>{row.isTop ? 'Yes' : ''}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
