import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Layout.css';

export default function Layout({ children }) {
  const location = useLocation();
  return (
    <div className="main-layout">
      <aside className="sidebar">
        <h2>India Travel</h2>
        <nav>
          <Link to="/dashboard" className={location.pathname === '/dashboard' ? 'active' : ''}>Map Overview</Link>
          <Link to="/worldmap" className={location.pathname === '/worldmap' ? 'active' : ''}>WorldMap View</Link>
          <Link to="/planner" className={location.pathname === '/planner' ? 'active' : ''}>Travel Planner</Link>
          <Link to="/journal" className={location.pathname === '/journal' ? 'active' : ''}>Travel Journal</Link>
          <Link to="/statistics" className={location.pathname === '/statistics' ? 'active' : ''}>Statistics</Link>
          <Link to="/settings" className={location.pathname === '/settings' ? 'active' : ''}>Settings</Link>
        </nav>
        <div className="sidebar-footer">
          <div className="user-info">
            <span role="img" aria-label="user">ðŸ‘¤</span> Omprakash Utaha
            <div className="user-status">Planning next trip</div>
          </div>
          <Link to="/login" className="logout-btn">Logout</Link>
        </div>
      </aside>
      <div className="main-content">
        {children}
      </div>
    </div>
  );
}
