import React, { useState, useMemo, useEffect, useRef } from 'react';
import { worldMapSVG } from '../assets/worldMapData';
import './WorldMap.css';

// Robustly add data-name attributes using DOMParser
function addDataNameToSVG(svgString) {
  if (typeof window === 'undefined' || !window.DOMParser) return svgString;
  const parser = new window.DOMParser();
  const doc = parser.parseFromString(svgString, 'image/svg+xml');
  const groups = doc.querySelectorAll('g');
  groups.forEach(g => {
    const title = g.querySelector('title');
    if (title) {
      const name = title.textContent;
      g.querySelectorAll('path, circle').forEach(el => {
        el.setAttribute('data-name', name);
      });
    }
  });
  return doc.documentElement.outerHTML;
}

// Helper to get the centroid of a country's path or circle
function getCountryCentroids(svgString) {
  const centroids = {};
  if (typeof window === 'undefined' || !window.DOMParser) return centroids;
  const parser = new window.DOMParser();
  const doc = parser.parseFromString(svgString, 'image/svg+xml');
  const groups = doc.querySelectorAll('g');
  groups.forEach(g => {
    const title = g.querySelector('title');
    const name = title ? title.textContent : null;
    if (!name) return;
    // Try to get a circle (for small countries)
    const circle = g.querySelector('circle');
    if (circle) {
      centroids[name] = {
        x: parseFloat(circle.getAttribute('cx')),
        y: parseFloat(circle.getAttribute('cy')),
      };
      return;
    }
    // Try to get a path and compute its bounding box center
    const path = g.querySelector('path');
    if (path && path.getBBox) {
      const bbox = path.getBBox();
      centroids[name] = {
        x: bbox.x + bbox.width / 2,
        y: bbox.y + bbox.height / 2,
      };
    }
  });
  return centroids;
}

function WorldMap({ selectedLocations = [], setSelectedLocations = () => {}, onLocationClick = () => {} }) {
  const [hoveredName, setHoveredName] = useState('');
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const [transform, setTransform] = useState({ x: 0, y: 0, scale: 1 });
  const svgContainerRef = useRef();
  const [pinPositions, setPinPositions] = useState([]);

  // Memoize the processed SVG string
  const processedSVG = useMemo(() => addDataNameToSVG(worldMapSVG), []);

  // Memoize centroids for pins
  const centroids = useMemo(() => getCountryCentroids(worldMapSVG), []);

  // Highlight selected countries and compute pin positions after SVG is rendered
  useEffect(() => {
    const svgEl = svgContainerRef.current?.querySelector('svg');
    if (!svgEl) return;
    // Remove previous selected
    svgEl.querySelectorAll('path.selected').forEach(p => p.classList.remove('selected'));
    // For pins
    const pins = [];
    selectedLocations.forEach(loc => {
      // Find all <g> with matching <title>
      const gs = Array.from(svgEl.querySelectorAll('g')).filter(
        g => g.querySelector('title') && g.querySelector('title').textContent === loc.name
      );
      let largestArea = 0;
      let pin = null;
      gs.forEach(g => {
        // Highlight all paths
        g.querySelectorAll('path').forEach(path => {
          path.classList.add('selected');
          // For pin: use the largest path's bbox
          try {
            const bbox = path.getBBox();
            const area = bbox.width * bbox.height;
            if (area > largestArea) {
              largestArea = area;
              pin = { x: bbox.x + bbox.width / 2, y: bbox.y + bbox.height / 2 };
            }
          } catch (e) {}
        });
        // If there is a circle, prefer it for small countries
        const circle = g.querySelector('circle');
        if (circle) {
          pin = {
            x: parseFloat(circle.getAttribute('cx')),
            y: parseFloat(circle.getAttribute('cy')),
          };
        }
      });
      if (pin) pins.push({ name: loc.name, ...pin });
    });
    setPinPositions(pins);
  }, [selectedLocations, processedSVG]);

  function handleLocationClick(event) {
    const id = event.target.id;
    const name = event.target.dataset.name;
    if (!id || !name) return;

    setSelectedLocations(prev => {
      const exists = prev.find(s => s.id === id);
      if (exists) {
        return prev.filter(s => s.id !== id);
      } else {
        return [...prev, { id, name }];
      }
    });
    onLocationClick({ id, name });
  }

  function onLocationMouseOver(event) {
    const name = event.target.dataset.name;
    if (name) {
      setHoveredName(name);
    } else {
      setHoveredName('');
    }
  }

  function onLocationMouseOut() {
    setHoveredName('');
  }

  function handleZoomIn() {
    setTransform(prev => ({
      ...prev,
      scale: Math.min(prev.scale * 1.2, 5)
    }));
  }

  function handleZoomOut() {
    setTransform(prev => ({
      ...prev,
      scale: Math.max(prev.scale * 0.8, 0.5)
    }));
  }

  function handleReset() {
    setTransform({ x: 0, y: 0, scale: 1 });
  }

  // Pin SVG
  const pinSVG = (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <g>
        <ellipse cx="12" cy="8.5" rx="5" ry="5.5" fill="#e53935" stroke="#b71c1c" strokeWidth="1.5"/>
        <path d="M12 14V22" stroke="#b71c1c" strokeWidth="2" strokeLinecap="round"/>
        <circle cx="12" cy="8.5" r="2" fill="#fff"/>
      </g>
    </svg>
  );

  return (
    <div className="world-map-container">
      <div
        className="world-map-svg"
        ref={svgContainerRef}
        dangerouslySetInnerHTML={{ __html: processedSVG }}
        onClick={handleLocationClick}
        onMouseOver={onLocationMouseOver}
        onMouseOut={onLocationMouseOut}
        style={{
          transform: `scale(${transform.scale})`,
          transformOrigin: 'center center',
        }}
      />
      {/* Render pins for selected countries */}
      <svg
        className="world-map-pins"
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          pointerEvents: 'none',
        }}
        viewBox="0 0 2754 1398"
      >
        {pinPositions.map(pin => (
          <g key={pin.name} transform={`translate(${pin.x},${pin.y})`}>
            <ellipse cx={0} cy={0} rx={18} ry={18} fill="#43a047" opacity="0.15" />
            <g transform="translate(-12,-24)">{pinSVG.props.children}</g>
          </g>
        ))}
      </svg>
      <div className="zoom-controls">
        <button className="zoom-button" onClick={handleZoomIn} title="Zoom In">+</button>
        <button className="zoom-button" onClick={handleZoomOut} title="Zoom Out">−</button>
        <button className="zoom-button" onClick={handleReset} title="Reset View">↺</button>
      </div>
      {hoveredName && (
        <div
          className="world-map-tooltip"
          style={{
            left: mousePos.x + 12,
            top: mousePos.y + 12
          }}
        >
          {hoveredName}
        </div>
      )}
    </div>
  );
}

export default WorldMap;
